/**
 * This code is the signin function for a user.
 * It finds the user by their username and checks if the password is valid.
 * If it is, it creates a token and sends back the user information.
 */
const config = require("../../../config/auth.config");
const db = require("../../../models");
const {
  clientIds
} = require("../../../config/clientIds.js");
const User = db.user;
const Role = db.role;
const Site = db.site;
const Site_Permissions = db.site_permissions;
const Global_Permissions = db.global_permissions;
const User_Site_Availability = db.user_site_availability;
const {
  google
} = require("googleapis");
var jwt = require("jsonwebtoken");
var bcrypt = require("bcryptjs");
const siteHelper = require("../../../helper/site");
const jwt_decode = require("jwt-decode");
const fetch = require("node-fetch");
const {
  Client
} = require("@microsoft/microsoft-graph-client");
const microsoft_scopes = ["https://graph.microsoft.com/.default"];
const redirectUri = 'http://localhost:8080/api/auth/handle-microsoft-redirect';
const {
  PublicClientApplication,
  ConfidentialClientApplication
} = require("@azure/msal-node");
const msal = require('@azure/msal-node');
const msalInstance = new msal.ConfidentialClientApplication({
  auth: {
    clientId: clientIds.microsoft.clientId,
    authority: `https://login.microsoftonline.com/${clientIds.microsoft.tenantId}`,
    clientSecret: clientIds.microsoft.clientSecret,
    redirectUri
  },
  system: {
    loggerOptions: {
      loggerCallback: (loglevel, message, containsPii) => {
        console.log(message);
      },
      piiLoggingEnabled: false,
      logLevel: msal.LogLevel.Verbose
    }
  }
});
const oauth2Client = new google.auth.OAuth2(clientIds.google.clientId, clientIds.google.clientSecret, `http://localhost:8080/api/auth/handleGoogleRedirect` // server redirect url handler
);

exports.signup = (req, res) => {
  const user = new User({
    username: req.body.username,
    email: req.body.email,
    password: bcrypt.hashSync(req.body.password, 8)
  });
  user.save((err, user) => {
    if (err) {
      res.status(500).send({
        message: err
      });
      return;
    }
    const site_name = siteHelper.getSiteName(req);
    Site.findOne({
      domain: site_name
    }).exec((err, site) => {
      if (err) {
        return err;
      }
      const new_site_product_availability = new User_Site_Availability({
        user_id: user._id,
        site_id: site._id
      });
      new_site_product_availability.save((err, site_permissions) => {
        if (err) {
          res.status(500).send({
            message: err
          });
          return;
        }
        let token = jwt.sign({
          id: user._id
        }, config.secret, {
          expiresIn: 86400 // 24 hours
        });

        res.status(200).send({
          id: user._id,
          username: user.email,
          email: user.email,
          accessToken: token,
          roles: []
        });
      });
    });
  });
};
exports.signin = (req, res) => {
  User.findOne({
    username: req.body.username
  }).exec((err, user) => {
    if (err) {
      res.status(500).send({
        message: err
      });
      return;
    }
    if (!user) {
      return res.status(404).send({
        message: "User Not found."
      });
    }
    var passwordIsValid = bcrypt.compareSync(req.body.password, user.password);
    if (!passwordIsValid) {
      return res.status(401).send({
        accessToken: null,
        message: "Invalid Password!"
      });
    }
    var token = jwt.sign({
      id: user._id
    }, config.secret, {
      expiresIn: 86400 // 24 hours
    });

    const site_name = siteHelper.getSiteName(req);
    Site.findOne({
      domain: site_name
    }).exec((err, site) => {
      if (err) {
        return err;
      }
      User_Site_Availability.findOne({
        user_id: user._id,
        site_id: site._id
      }).exec((err, user_site_availability) => {
        if (err || !user_site_availability) {
          res.status(403).send({
            message: "You do not have access to this site"
          });
          return;
        }
        Site_Permissions.findOne({
          user_id: user._id,
          site_id: site._id
        }).populate("roles", "-__v").exec((err, site_permissions) => {
          if (err) {
            return err;
          }
          Global_Permissions.findOne({
            user_id: user._id
          }).populate("roles", "-__v").exec((err, global_permissions) => {
            if (err) {
              return err;
            }
            let all_permissions = [];
            if (site_permissions) {
              all_permissions = all_permissions.concat(site_permissions.roles);
            }
            if (global_permissions) {
              all_permissions = all_permissions.concat(global_permissions.roles);
            }
            if (!all_permissions) {
              return res.status(403).send({
                message: "You do not have access to this site"
              });
            }
            var authorities = [];
            for (let i = 0; i < all_permissions.length; i++) {
              authorities.push("ROLE_" + all_permissions[i].name.toUpperCase());
            }
            res.status(200).send({
              id: user._id,
              username: user.username,
              email: user.email,
              accessToken: token,
              roles: authorities
            });
          });
        });
      });
    });
  });
};
exports.oAuthSignup = (req, res) => {
  let token = req.body.credential;
  let type = req.body.type;
  let userInfo = {};
  if (type == 'google') {
    oauth2Client.setCredentials({
      access_token: token
    });
    var oauth2 = google.oauth2({
      auth: oauth2Client,
      version: 'v2'
    });
    oauth2.userinfo.get(function (err, userdata) {
      userInfo.email = userdata.data.email, userInfo.username = userInfo.email;
      if (err) {
        console.log(err);
      } else {
        // Email
        User.findOne({
          email: userdata.data.email
        }).exec((err, user) => {
          if (user) {
            res.status(400).send({
              message: "Failed! Email is already in use!"
            });
            return;
          }
          const newUser = new User({
            ...userInfo
          });
          newUser.save((err, savedUser) => {
            if (err) {
              res.status(500).send({
                message: err
              });
              return;
