          message: "Failed! Email is already in use!"
        });
        return;
      }
      const newUser = new User({
        ...userInfo
      });
      newUser.save((err, savedUser) => {
        if (err) {
          res.status(500).send({
            message: err
          });
          return;
        }
        const site_name = siteHelper.getSiteName(req);
        Site.findOne({
          domain: site_name
        }).exec((err, site) => {
          if (err) {
            return err;
          }
          const new_site_product_availability = new User_Site_Availability({
            user_id: savedUser._id,
            site_id: site._id
          });
          new_site_product_availability.save((err, user_site_availability) => {
            if (err) {
              res.status(500).send({
                message: err
              });
              return;
            }
            let user_token = jwt.sign({
              id: savedUser._id
            }, config.secret, {
              expiresIn: 86400 // 24 hours
            });

            res.status(200).send({
              id: savedUser._id,
              username: savedUser.email,
              email: savedUser.email,
              accessToken: user_token,
              roles: []
            });
          });
        });
      });
    });
  }
};
exports.oAuthSignin = (req, res) => {
  const token = req.body.credential;
  const type = req.body.type;
