                .exec((err, site_permissions) => {
                    if (err) {
                        res.status(500).send({ message: err });
                        return;
                    }

                    if (!site_permissions) {
                        return res.status(404).send({ message: "SitePermissions Not found." });
                    }

                    res.status(200).send(site_permissions);
                });
        });
};

