exports.signin = (req, res) => {
  User.findOne({
    username: req.body.username
  }).exec((err, user) => {
    if (err) {
      res.status(500).send({
        message: err
      });
      return;
    }
    if (!user) {
      return res.status(404).send({
        message: "User Not found."
      });
    }
    var passwordIsValid = bcrypt.compareSync(req.body.password, user.password);
    if (!passwordIsValid) {
      return res.status(401).send({
        accessToken: null,
        message: "Invalid Password!"
      });
    }
    var token = jwt.sign({
      id: user._id
    }, config.secret, {
      expiresIn: 86400 // 24 hours
    });

    const site_name = siteHelper.getSiteName(req);
    Site.findOne({
      domain: site_name
    }).exec((err, site) => {
      if (err) {
        return err;
      }
      User_Site_Availability.findOne({
        user_id: user._id,
        site_id: site._id
      }).exec((err, user_site_availability) => {
        if (err || !user_site_availability) {
          res.status(403).send({
            message: "You do not have access to this site"
          });
          return;
        }
        Site_Permissions.findOne({
          user_id: user._id,
          site_id: site._id
        }).populate("roles", "-__v").exec((err, site_permissions) => {
          if (err) {
            return err;
          }
          Global_Permissions.findOne({
            user_id: user._id
          }).populate("roles", "-__v").exec((err, global_permissions) => {
            if (err) {
              return err;
            }
            let all_permissions = [];
            if (site_permissions) {
              all_permissions = all_permissions.concat(site_permissions.roles);
            }
            if (global_permissions) {
              all_permissions = all_permissions.concat(global_permissions.roles);
            }
            if (!all_permissions) {
              return res.status(403).send({
                message: "You do not have access to this site"
              });
            }
            var authorities = [];
            for (let i = 0; i < all_permissions.length; i++) {
              authorities.push("ROLE_" + all_permissions[i].name.toUpperCase());
            }
            res.status(200).send({
              id: user._id,
              username: user.username,
              email: user.email,
              accessToken: token,
              roles: authorities
            });
          });
        });
      });
    });
  });
};
